import React, {Fragment} from 'react'

const Remonter = () => {

    window.onscroll = () => {hauteur_scrool()};

    function hauteur_scrool(){
       const hauteur_screen = window.scrollY
       const rien = document.querySelector('.global_remonter')

        if(hauteur_screen >= 100){
            rien.style.display = "unset"
            rien.style.margin = "unset"
            
            console.log(rien)
        }else{
            rien.style.display = "none"

            }
    }

    return(
        <div className="global_remonter">      
            <a href="#remonter">
                <div className="remonter"><i className='fas fa-arrow-alt-circle-up' style={{fontSize:'36px', color:'white'}}></i></div>       
            </a>
        </div>   
    )
}

export default Remonter
