import React, {Fragment, useState, useEffect} from 'react';
import Navigation from './Navigation';
import axios from 'axios';



const Programmation = () => {

    const [data, setData] = useState([]);

    

    const [foodState, setFoodState] = useState(["rap_chante"]);
  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(
        "/memory_admin/controller/programme/programme_api.php",
      );

      setData(result.data);

    };
    fetchData();

  }, [])

  // const handleClick = (key,e) => {  
  //   e.preventDefault();    
  //   console.log(key);  
  // }

  return (
        <Fragment>
            <Navigation />
            <select name="" value={foodState} onChange={(e) => {

                const selectedFood = e.target.value ;

                setFoodState(selectedFood)


            }}>
                <option value="rap_chante">Rap chant√©</option>
                <option value="rap_hardcore">Rap hardcore</option>
            </select>

            {/* {console.log(foodState)} */}


            {data.filter(musique => musique.type_rap === foodState ).map((item, key) => ( // enlever le filter
                <li className="informations_generales" key={key}>
                    <ul>
                        <div className="sous_titre_et_contenu_information_faq">
                            <li>
                            <div>{ item.nom_artiste }</div>
                            <div>{ item.nom_scene }</div>
                            </li>
                        </div>
                        
                    </ul>
                </li> 
            ))}
        </Fragment>
    )
}

export default Programmation
