import React, {useRef} from 'react'

const Remonter = () => {
    const inputEl = useRef(null);
    const onButtonClick = {
      // `current` fait référence au champ textuel monté dans le DOM
      ...inputEl.current.style.backgroundColor === "red" ? inputEl.current.style.backgroundColor = "green" : inputEl.current.style.backgroundColor = "yellow"

    };
    return (
        <>
          <input ref={inputEl} type="text" />
          <button onClick={onButtonClick}>Donner le focus au champ</button>
        </>
      );
    }

export default Remonter
