import axios from 'axios';
import Navigation from './Navigation';
import Msg_importants from './Msg_important'



const Actualites = () => {
    const [data, setData] = useState([]);

    useEffect(() => {

        const fetchData = async () => {
        const result = await axios(
        "/memory_admin/controller/information/actualites_api.php",
        );

        setData(result.data);
        console.log("updated");
        };
        fetchData();
        console.log("mounted");

    }, [])
    // const handleClick = (key,e) => {  
    //   e.preventDefault();    
    //   console.log(key);  
    // }

  return (
    <Fragment> 
        <div className="information_pratiques_all_all">
            <div className="informations_pratiques_all">
                <h1>Actualit√©s</h1>
            
                    {data.map((item, key) => (
                            <li className="informations_generales" key={key}>
                                <ul>
                                    <div className="sous_titre_et_contenu_information_faq">
                                        <li>
                                            <div>{ item.contenu_actualites}</div>
                                        </li>
                                    </div>
                                </ul>
                            </li>
                        // </a>
                        ))}
                    {/* </div>
                </ul> */}
            </div>
        </div>
    </Fragment> 
  ) 
}

export default Actualites;
