import React, { useState } from 'react'; 


const Example = () => {
  const [count, setCount] = useState(0); 
  const Incrementation = () => setCount(count + 1);
  const RemettreZero = () => setCount(0);
  const DeIncrementation = () => setCount(count - 1);
  if((count) == 1){
      alert('salut11');
  }else{
    setCount(count) == 0;
  }


  return (
    <div>
      <p>Vous avez cliqué {count} fois</p>
      <button onClick={Incrementation}>
          +
     </button>

     <button onClick={DeIncrementation}>
          -
     </button>
     <button onClick={RemettreZero}>
          Remettre à Zéro
     </button>
    </div>
  );
}

export default Example;
