import React, {Fragment, useState, useEffect} from 'react';
import axios from 'axios';



const Type_rap_prgm = () => {

    const [data, setData] = useState([]);

    const [typeOfRap, setTypeOfRap] = useState();
    
    const [filterData, setFilterData] = useState([]);

    const [autreFilter, setAutreFilter] = useState();

    const [autreFilter1, setAutreFilter1] = useState();


  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(
        "/memory_admin/controller/programme/programme_api.php",
      );

      setData(result.data);

    };
    fetchData();

  }, [])


  useEffect(() => {

    if(typeOfRap && autreFilter && !autreFilter1){
        const filterTypeRap = data.filter(musique => musique.type_rap === typeOfRap && musique.nom_scene === autreFilter)
        setFilterData(filterTypeRap)
        return
    }

    if(typeOfRap && autreFilter1 && !autreFilter){
        const filterTypeRap = data.filter(musique => musique.type_rap === typeOfRap && musique.nom_artiste === autreFilter1)
        setFilterData(filterTypeRap)
        return
    }

    if(autreFilter1 && autreFilter && !typeOfRap){
        const filterTypeRap = data.filter(musique => musique.nom_artiste === autreFilter1 && musique.nom_scene === autreFilter)
        setFilterData(filterTypeRap)
        return
    }

    if(autreFilter1 && autreFilter &&  typeOfRap){
        const filterTypeRap = data.filter(musique => musique.type_rap === typeOfRap && musique.nom_scene === autreFilter && musique.nom_artiste === autreFilter1 )
        setFilterData(filterTypeRap)
        return
    }

    if(typeOfRap || autreFilter || autreFilter1 ){

        const filterTypeRap = data.filter(musique => musique.nom_scene === autreFilter || musique.type_rap === typeOfRap || musique.nom_artiste === autreFilter1)
        setFilterData(filterTypeRap)
        return
    }

    // if(typeOfRap || autreFilter ){

    //     const filterTypeRap = data.filter(musique => musique.nom_scene === autreFilter || musique.type_rap === typeOfRap)
    //     setFilterData(filterTypeRap)
    //     return
    // }

    // if(autreFilter || autreFilter1 ){

    //     const filterTypeRap = data.filter(musique => musique.nom_scene === autreFilter || musique.nom_artiste === autreFilter1)
    //     setFilterData(filterTypeRap)
    //     return
    // }

    // if(typeOfRap || autreFilter1 ){

    //     const filterTypeRap = data.filter(musique => musique.nom_artiste === autreFilter1 || musique.type_rap === typeOfRap )
    //     setFilterData(filterTypeRap)
    //     return
    // }

    setFilterData(data)
    return
    
  }, [data, typeOfRap, autreFilter, autreFilter1])


  var result = filterData.length;

if(result <=1){
    result = result + "Resultat"    
}else{

}



  // const handleClick = (key,e) => {  
  //   e.preventDefault();    
  //   console.log(key);  
  // }

  return (
        <Fragment>
            <select name="" value={typeOfRap} onChange={(e) => {

                const selectedFoodT = e.target.value ;
                console.log(typeOfRap)

                setTypeOfRap(selectedFoodT)


            }}>
                <option value="">--- Type de Musique ---</option>
                <option value="rap_chante">Rap chanté</option>
                <option value="rap_hardcore">Rap hardcore</option>
                <option value="rap_ancien">Rap ancien</option>
            </select>


            <select name="" value={autreFilter} onChange={(e) => {

            const selectedFood = e.target.value ;
                console.log(autreFilter)
            setAutreFilter(selectedFood)


            }}>
            <option value="">--- Type de Musique ---</option>
            <option value="Scene 1">Scene 1</option>
            <option value="scene numero 2">Scene 2</option>

            
            </select>
            

            <select name="" value={autreFilter1} onChange={(e) => {

            const selectedFoodTT = e.target.value ;
                setAutreFilter1(selectedFoodTT)


            }}>
            <option value="">--- Type de Musique ---</option>
            <option value="booba">booba</option>
            <option value="Ninho">ninho</option>


            </select>



            {/* {console.log(typeOfRap)} */}
            {/* <div> ({result} == 1 ?  {result} Résultat : {result} Résultats) </div> */}
            <div> {result}</div>

            {filterData.map((item, key) => ( // enlever le filter
                <div className={`template${key % 2}`} key={key}>
                    
                    {console.log(filterData.size)}
                    <div className="template_grid_1">
                        <img className="img_template_grid" src={`data:image/png;base64, ${item.image_programme}`} alt="" />
                    </div>
                    <div className="template_grid_2">
                        <div>{ item.nom_artiste }</div>
                        <div>{ item.nom_scene }</div>
                        <div>{ item.date_formatage }</div>
                        <div>{ item.heures_prog_debut } - {item.heures_prog_fin}</div>
                    </div>
                </div> 
            ))}
        </Fragment>
    )
}

export default Type_rap_prgm
