import React, {Fragment, useState} from 'react';
import Navigation from './Navigation';


const Programmation = () => {

    const [foodState, setFoodState] = useState(tout)

    useEffect(() => {

        const fetchData = async () => {
        const result = await axios(
        "/memory_admin/controller/information_faq/information_faq_api.php",
        );
                
        setData(result.data);
        console.log("updated");
        };
        fetchData();
        console.log("mounted");
        return 

    }, [])

    return(
        <Fragment>
            <Navigation />
            <select name="" onChange={(e) => {
                const selectedFood = e.target.value;
                // console.log(e.target.value)
                setFoodState(selectedFood);
            }}>
                <option value={"tout"}>Tout</option>
                <option value={"rap"}>Rap</option>
                <option value={"classique"}>Classique</option>
                <option value={"rock"}>Rock</option>
            </select><br></br>

            {data.filter(musique => musique.style == foodState).map((item, key) => ( // enlever le filter
                <li className="informations_generales" key={key}>
                    <ul>
                        <div className="sous_titre_et_contenu_information_faq">
                            <li>
                                <div>{ item.contenu_information_generales}</div>
                            </li>
                        </div>
                    </ul>
                </li> 
            ))}
        </Fragment>
    )
}

export default Programmation
