import React, { useState, useEffect } from "react";
import axios from "axios";

const Weather = () => {
//   const [value, setValue] = useState("");
  const [temp, settemp] = useState("");
  const [typecity, settypecity] = useState("");
  const [city, setcity] = useState("Delhi");
  const [disc, setdisc] = useState("");

  const citySelect = (e) => {
    e.preventDefault();
    setcity(typecity);
  };
  useEffect(() => {
    axios(
      `http://api.openweathermap.org/data/2.5/weather?q=Paris&appid=b391fd1e50ca575d8a12938c64fca49e`
    ).then((res) => {
      setValue(res.data);
      settemp(res.data.main);
      setdisc(res.data.weather[0].description);
    });
  }, [city]);

  return (
    <div>
            {(Math.floor(temp.temp_min - 273.15) >= 5 ? `Pensez à bien vous hydrater  : ${Math.floor(temp.temp_min - 273.15)}° C` : "")}
         
    </div>
  );
};

export default Weather;
