import React, {Fragment, useState, useEffect} from 'react'
import axios from 'axios';
import Shay from './img/shay.png'


const Tete_affiche = () => {
    const [data, setData] = useState([]);

    useEffect(() => {

        const fetchData = async () => {
        const result = await axios(
        "/memory_admin/controller/tete_affiche/tete_affiche_api.php",
        );

        setData(result.data);
        console.log("updated");
        };
        fetchData();
        console.log("mounted");
    }, [])
    // const handleClick = (key,e) => {  
    //   e.preventDefault();    
    //   console.log(key);  
    // }
    
    return (
        <Fragment> 
            <div onClick={() => document.getElementById('menuopen').style.marginLeft = "-80.2%" } >
            {data./*filter(name => name.id === 286).*/map((item, key) => (

            <div class="artiste_tete_affiche" key={key}>
                <img className="image_tete_affiche" src={`data:image/png;base64, ${item.image_tete_affiche}`} alt=""/>
                <div class="div_opacity_dans_texte"></div>
                <div class="text_sous_artiste_tete_affiche">{ item.nom_artiste_tete_affiche } <br />{ item.date_concert}</div>
            </div>
            ))}
            </div>
        </Fragment>        
    )   
}

export default Tete_affiche
