import React from 'react'

const Remonter = () => {
    const fleche_remonter = () => {
        <a href="#remonter">
        <div className="remonter"><i className='fas fa-arrow-alt-circle-up' style={{fontSize:'36px', color:'white'}}></i></div>       
    </a>
    }
    window.onscroll = function() {hauteur_scrool()};
    function hauteur_scrool(){
       const hauteur_screen = window.scrollY
        if(hauteur_screen >= 100){
            <div>{fleche_remonter()}</div>
        }else{
            <div>{fleche_remonter()}</div>
        }
    }

    return(
        <div>{hauteur_scrool()}</div>

    )
}

export default Remonter
