import React, {Fragment, useState, useEffect} from 'react';
import axios from 'axios';



const Actualites = () => {
    const [data, setData] = useState([]);

    useEffect(() => {

        const fetchData = async () => {
        const result = await axios(
        "/memory_admin_en/controller/information/actualites_api.php",
        );

        setData(result.data);
        console.log("updated");
        };
        fetchData();
        console.log("mounted");

    }, [])
    // const handleClick = (key,e) => {  
    //   e.preventDefault();    
    //   console.log(key);  
    // }

  return (
    <Fragment> 
        <div className="information_pratiques_all">
            <div className="informations_pratiques_all">
                <h1>News</h1>
            
                    {data.map((item, key) => (
                            <li className="informations_generales" key={key}>
                                <ul>
                                    <div className="actua">
                                        <li>
                                            <div>{ item.contenu_actualites}</div>
                                        </li>
                                    </div>
                                </ul>
                            </li>
                        // </a>
                        ))}
                    {/* </div>
                </ul> */}
            </div>
        </div>
    </Fragment> 
  ) 
}

export default Actualites;
