import React, { useState, useEffect, Fragment } from "react";
import axios from "axios";
import Soleil from './img/soleil.png'
const Meteo = () => {

  const [temp, settemp] = useState([]);
  const ville = "Paris"

  useEffect(() => {
    axios(`http://api.openweathermap.org/data/2.5/weather?q=${ville}&appid=b391fd1e50ca575d8a12938c64fca49e`).then((res) => {
      settemp(res.data.main);
    });
  }, []);

  return (
    <Fragment>
        <div className="meteo">
            {(Math.round(temp.temp - 273.15) >= 5 ? `${Math.round(temp.temp - 273.15)}° C : Pensez à vous hydrater` : "")}
            <img className="soleil" src={Soleil} alt="" />
        </div>
    </Fragment>
  );
};

export default Meteo;
