import React, {Fragment, useState, useEffect} from 'react';
import axios from 'axios';

const Direct = () => {

    const [data, setData] = useState([]);

    useEffect(() => {
        const fetchNom_scene = async () => {
            const result = await axios(
            "/memory_admin/controller/distinc_select_api.php",
            );
    
            setData(result.data);
            console.log("updated");
        }
            fetchData();
            console.log("mounted");
    
    }, [])


    const Atm = () => {
        let date1 = new Date();

        let dateLocale = date1.toLocaleString('fr-FR',{
            weekday: 'long',
            year: 'numeric',
            month: 'numeric',
            day: 'numeric',
            hour: 'numeric',
            minute: 'numeric',
            second: 'numeric'});
        return (
            dateLocale
        )
    }
    return(
        <Fragment>
            <div className="actu_concert">Actuellement en concert</div>
            
            {data.filter(name => name.datetime).map((item,key) => (
                <div> {item.datetime}</div>
            ))}

        </Fragment>
    )
}

export default Direct
