const proprietes = {
  duration : 50000,
  transitionDuration: 500,
  // transitionDuration: false,
  infinite : true,
  indicators: true,
  arrows: true
}


const Accueil = () => {
  const [data, setData] = useState([]);


  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(
        '/memory_admin/controller/msg_important_fr/msg_important_api.php',
      );

      setData(result.data);
      console.log("updated");
    };
    fetchData();
    console.log("mounted");

  }, [])
  const handleClick = (key,e) => {  
    e.preventDefault();    
    console.log(key);  
  }

  return (
    <Fragment> 
      <div>Accueil </div>
      <ul>
        <div className="containerSlide">
          <Slide {...proprietes}>
          {data./*filter(name => name.id === 286).*/map((item, key) => (
            // {data.map((item, key) =>(
              <a onClick={(e) => handleClick(key,e)} key={key} >
              {/* <a href="https://google.com"> */}
                <li className={"article"} key={key}>
                  {/* <p>{item.image}</p> */}


                  {/* <img src={`data:image/png;base64, ${item.image}`} alt="" /> */}
                  <p className="titre">{item.titre_msg_impotant}</p>


                  {/* <p>{ key }</p> */}
                  <div className="each-slide">
                    <div>
                      {/* <img src={image1} alt="image1" /> */}
                    </div>
                  </div>
                </li>
              </a>
            ))}
          </Slide>
        </div>
      </ul>
    </Fragment> 
  ) 
}

export default Accueil;
