import React, {Fragment, useState, useEffect} from 'react';
import axios from 'axios';
import { Slide } from 'react-slideshow-image';

import Meteo from '../Meteo'

const proprietes = {
  duration : 10000,
  transitionDuration: 500,
  // transitionDuration: false,
  infinite : true,
  indicators: true,
  arrows: false
}

