import React, { useState, useEffect } from "react";
import axios from "axios";

const Weather = () => {

  const [temp, settemp] = useState("");

  const citySelecta = (e) => {
    e.preventDefault();
  };
  useEffect(() => {
    axios(
      `http://api.openweathermap.org/data/2.5/weather?q=Paris&appid=b391fd1e50ca575d8a12938c64fca49e`
    ).then((res) => {
      settemp(res.data.main);
    });
  }, []);

  return (
    <div>
            {(Math.round(temp.temp - 273.15) >= 5 ? `Pensez à bien vous hydrater  : ${Math.round(temp.temp - 273.15)}° C` : "")}
         
    </div>
  );
};

export default Weather;
