import React, {Fragment, useState, useEffect} from 'react';
import Navigation from './Navigation';
import axios from 'axios';



const Programmation = () => {

    const [data, setData] = useState([]);

    

    const [foodState, setFoodState] = useState();
  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(
        "/memory_admin/controller/programme/programme_api.php",
      );

      setData(result.data);

    };
    fetchData();

  }, [])
const test = () => {return (<select name="" value={foodState} onChange={(e) => {
    // const selectedFood = ({setDataSelect: e.target.value }) ;
    const selectedFood = e.target.value ;
    // selectedFood.bar = {};
    // console.log(selectedFood)
    setFoodState(selectedFood)

    // console.log(setDataSelect(selectedFood))

}}>
    <option value="rap_chante">Rap chant√©</option>
    <option value="rap_hardcore">Rap hardcore</option>
</select>
)}
const rierrrn = () => {return (data.filter(musique => musique.type_rap == foodState ).map((item, key) => ( // enlever le filter
    <li className="informations_generales" key={key}>
        <ul>
            <div className="sous_titre_et_contenu_information_faq">
                <li>
                <div>{ item.nom_artiste }</div>
                <div>{ item.nom_scene }</div>
                </li>
            </div>
            
        </ul>
    </li> 
)))}
  // const handleClick = (key,e) => {  
  //   e.preventDefault();    
  //   console.log(key);  
  // }

  return (
        <Fragment>
            <Navigation />
            {test}
            {rierrrn}

            
        </Fragment>
    )
}

export default Programmation
