import React, { useState } from "react";


const Contact = () => {  
    
    
    function calcul(){}
    

  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");
  const erreur = document.querySelector('.form-message')

  const handleSubmit = (e) => {
    e.preventDefault();

    if(name != "" && email != "" && message != ""){
        var mailformat = /^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-])/;

        if(email.match(mailformat)){
            // console.log("mailgood")
            sendFeedback("template_a34vo2a", {
                name,
                email,
                message,
            });
        }
        else{
            erreur.innerHTML = "Format E-mail invalide"
        }  
    }else{
        function calcul(){
            document.querySelector("#rien").innerHTML = '<div style="color: red;text-align:center;">Email ou Mot de passe vide</div>'
        }
        erreur.innerHTML = "Champ(s) incomplet"
    }
    };

  const sendFeedback = (templateId, variables) => {
    window.emailjs
      .send("service_h5mh0lp", templateId, variables)
      .then((res) => {
        // console.log('success !');
        setName("");
        setEmail("");
        setMessage("");
        erreur.innerHTML = "Mail envoyé"
      })
      .catch(
        (err) =>
          document.querySelector('.form-message').innerHTML =
            "Une erreur s'est produite, veuillez réessayer.")
  };

  return (
      <div  onload="calcul()">
    <form className="contact-form">
      <h2>Contactez-nous</h2>
      <div className="form-content">
        <input
          type="text"
          id="name"
          name="name"
          onChange={(e) => setName(e.target.value)}
          placeholder="nom *"
          value={name}
          autoComplete="off"
        />
     
     
        <div className="email-content">
          <input
            type="mail"
            id="email"
            name="email"
            onChange={(e) => setEmail(e.target.value)}
            placeholder="email *"
            value={email}
            autoComplete="off"
          />
        </div>
        <textarea
          id="message"
          name="message"
          onChange={(e) => setMessage(e.target.value)}
          placeholder="message *"
          value={message}
        />
      </div>
      <input
        className="button"
        type="button"
        value="Envoyer"
        onClick={handleSubmit}
      />
      <div className="form-message"></div>
    </form>
    </div>
  );
};

export default Contact;
