import React, {useRef} from 'react'

const Remonter = () => {
    const inputEl = useRef(null);
    const onButtonClick = () => {
      // `current` fait référence au champ textuel monté dans le DOM
      inputEl.current.focus();
    };
    return(
        <a href="#remonter">
                  <input ref={inputEl} type="text" />
            <div onclick={onButtonClick()}className="remonter"><i className='fas fa-arrow-alt-circle-up' style={{fontSize:'36px', color:'white'}}></i></div>
        </a>
        
    )
}

export default Remonter
