import React, {Fragment, useState, useEffect} from 'react';
import axios from 'axios';
import Navigation from './Navigation';
import Msg_importants from './Msg_important'



const Informations = () => {
    const [data, setData] = useState([]);

    useEffect(() => {

        const fetchData = async () => {
        const result = await axios(
        "/memory_admin/controller/information/information_api.php",
        );

        setData(result.data);
        console.log("updated");
        };
        fetchData();
        console.log("mounted");

    }, [])
    // const handleClick = (key,e) => {  
    //   e.preventDefault();    
    //   console.log(key);  
    // }

  return (
    <Fragment> 
        <Navigation />
        <Msg_importants />
        <div className="informations_pratiques_all">
            <h1>Informations Générales</h1>
         
                {data.map((item, key) => (
                        <li className={"informations_generales"} key={key}>
                            <ul>
                                <li>
                                        <div className="titre_information_faq">Informations générales</div>
                                </li>
                                <div className="sous_titre_et_contenu_information_faq">
                                    <li>
                                        <div className="sous_titre_information_faq">{ item.sous_titre_information_faq}</div>
                                    </li>
                                    <li>
                                        <div>{ item.contenu_information_faq}</div>
                                    </li>
                                </div>
                            </ul>
                        </li>
                    // </a>
                    ))}
                {/* </div>
            </ul> */}
        </div>
    </Fragment> 
  ) 
}

export default Informations;
