import React, { Fragment, useState } from "react";
import Navigation from './Navigation'
import Footer from './Footer'

const Contact = () => {

  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");
  const [error_result, setError_result] = useState("");
  // const erreur = document.querySelector('.form-message')

  const handleSubmit = (e) => {
    
    e.preventDefault();

    if(name != "" && email != "" && message != ""){
        var mailformat = /^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-])/;

        if(email.match(mailformat)){
            // console.log("mailgood")
            sendFeedback("template_a34vo2a", {
                name,
                email,
                message,
            });
        }
        else{
          setError_result                           
        }  
    }else if(erreur != null){
        erreur.innerHTML = "Champ(s) incomplet"
    }else{
        alert("Tous les champs sont vides ! üòí")
    }
    };

  const sendFeedback = (templateId, variables) => {

    window.emailjs
      .send("service_h5mh0lp", templateId, variables)
      .then((res) => {
        // console.log('success !');
        setName("");
        setEmail("");
        setMessage("");
        erreur.innerHTML = "Mail envoy√©"
      })
      .catch(
        (err) =>
          document.querySelector('.form-message').innerHTML =
            "Une erreur s'est produite, veuillez r√©essayer.")
  };

  return (
      <Fragment>
          <Navigation />
    <form className="contact-form">
      <h2>Contactez-nous</h2>
      <div className="form-content">
        <input
          type="text"
          id="name"
          name="name"
          onChange={(e) => setName(e.target.value)}
          placeholder="nom *"
          value={name}
          autoComplete="off"
        />
     
     
        <div className="email-content">
          <input
            type="mail"
            id="email"
            name="email"
            onChange={(e) => setEmail(e.target.value)}
            placeholder="email *"
            value={email}
            autoComplete="off"
          />
        </div>
        <textarea
          id="message"
          name="message"
          onChange={(e) => setMessage(e.target.value)}
          placeholder="message *"
          value={message}
        />
      </div>
      <input
        className="button"
        type="button"
        id="button"
        value="Envoyer"
        onClick={handleSubmit}
      />
      <div className="form-message">{ error_result }</div>
    </form>
    <Footer />
    </Fragment>
  );
};

export default Contact;
